daci    //2 - Račun blokiran?><?php include('kdomic_header.php'); ?><section>    <form id="loginForm" action="kdomic_login.php" method="POST">        <div class="login">            <div id="loginL">                <img src="images/login_img.gif" alt="Prijava" /> <!-- http://intranet.teamlease.com/Images/login_img.gif -->            </div>            <div id="loginR">                <?php                     if(isset($_GET['error']))                        if($_GET['error']=='1')                                                echo '<p class="errorLabelMessage">Krivi podaci za prijavu!</p>';                        else if ($_GET['error']=='2')                               echo '<p class="errorLabelMessage">Vaš korisnički račun je blokiran</p>';                ?>                <table class="loginTable">                    <tr>                        <td><label for="korisnicko">Korisničko ime</label></td>                        <td><input type="text" name="korisnicko" id="korisnicko" placeholder="Korisničko ime" value="<?php echo $user; ?>" autofocus /></td>                    </tr>                    <tr>                        <td><label for="lozinka">Lozinka</label></td>                        <td><input type="password" name="lozinka" id="lozinka" placeholder="Lozinka"/></td>                    </tr>                    <tr>                        <td><label for="zapamti">Zapamti me</label></td>                        <td><input type="checkbox" name="zapamti" id="zapamti" /></td>                    </tr>                    <tr>                        <td colspan="2" class="center"><input type="submit" name="submit" id="posalji" value="Pošalji"/></td>                    </tr>                </table>            </div>        </div>    </form>    <p>        Napomena:<br>        Budući da u bazi ne postoji atribut korisničko ime nego se verifikacija vrši putem emaila u polje korisničko ime potrebno je upisati:<br><br>       